let getStarted = false;
let sliderBG, sliderSpeed;
fc = 0

function setup() {
	createCanvas(windowWidth, windowHeight);
	//background(0);
	//colorMode(HSB, 100)

	guiHolder = createDiv()
		.class('guiHolder')

	//sliders n buttons

		//to activate background 
	button = createButton("click me");
	button.parent(guiHolder)
	button.class('button')

	button.mouseClicked(function() {
		getStarted = !getStarted;
	});

	sliderBG = createSlider(0, 255, 0, .25); // minValue, maxValue, defaultValue, stepSize
	sliderBG.parent(guiHolder)
	sliderBG.class('slider')

	sliderSpeed = createSlider(0, 70, 8, .1);
	sliderSpeed.parent(guiHolder)
	sliderSpeed.class('slider')


	sliderRotate = createSlider(0, 360, 0, .1);
	sliderRotate.parent(guiHolder)
	sliderRotate.class('slider')


	sliderSize = createSlider(40, 500, 300, .10);
	sliderSize.parent(guiHolder)
	sliderSize.class('slider')


	createElement('style', ` 
  *
    .guiHolder{ 
      position: fixed;
      right: 100px; 
      top: 60px;
    } 

    .slider{ 
      width: 200px; 
      -webkit-appearance: none;
	background:blue;
	border-radius:5px;
	height:5px;
	margin:12px;
    }
    .button{
    	width:70px;
    	height:40px;
	color: blue;
	background: white;
	border-radius:3px;
	margin:12px;
    }
    `)
}



function draw() {

	//what do sliders affect
	let val = sliderBG.value();
	background(sliderBG.value());

	//background start
	if(getStarted) {
		background(sliderBG.value())
	}

	//background after click
	for(let i = 0; i < width; i += 7) {
		if(getStarted) {
			stroke(frameCount * 0, 0, random(255));
			strokeWeight(0.5)
			line(i, 0, i, height);
		}
	}
	translate(width / 2, height / 2);
	//the fork !!
	//noStroke()
	fill(255 - sliderBG.value())

	fc += sliderRotate.value()

	beginShape()
	for(c of fork) {
		curveVertex((c[0] - .5) * sliderSize.value() * 3.8 + random(sliderSpeed.value()), (c[1] - .5) * sliderSize.value() * 2 + random(sliderSpeed.value()))
		rotate(fc % width, height + sin(frameCount*.05)*5)

	}
	endShape(CLOSE)

}
