let txt = "A"
let fontArray = ['Futura-Medium-01', 'Spartan-Bold', 'Spartan-Regular', 'Arapey-Regular', 'IBMPlexMono-Medium', 'Vogue']
let fontPaths = []

function setup() {
	createCanvas(windowWidth, windowHeight)
	fontButton = createButton('Change Font')
	guiSetup()
	textFont(fontPaths[0])
}

function draw() {
	fc = frameCount * 30 % width
	fill(cp.color())
	background(invertColor(cp.value()))
	translate(width / 2, height / 2.5)
	textAlign(CENTER, CENTER)
	//2 strech
	scale(sliderStrech.value(), 1, 10)

	//3 size
	textSize(sliderSize.value())

	//6 angle
	angle = sliderAngle.value()
	rotate(angle)

	//7 stroke
	strokeWeight(sliderStroke.value())
	stroke(cp.value())

	//8 shear
	if(checkboxShearY.checked()) {
		sliderShearY.value(sin(frameCount * 0.1))
	} else {}
	shearY(sliderShearY.value(), 1, 10)

	//8	letter
	textStyle(NORMAL)
	text(txt, 0, 0)
	stroke(20)
}

function guiReset() {
	sliderStrech.value(1)
	sliderSize.value(600)
	sliderAngle.value(TWO_PI)
	sliderStroke.value(0)
	checkboxShearY.checked(false)
	sliderShearY.value(0)
	cp.value('#FFFFFF')
	txt = 'A'
	txtInput.value(txt)
	textFont(fontPaths[0])
}

