let credits = { 
    "student_names": "ted davis", 
    "recode_id": "zotero2-2604593.ULDKFWJD", 
    "recode_timestamp": 0, 
    "recode_title": "Namensanimation_Corcodel_Krämer", 
    "recode_designer": "Harriet Corcodel + Anne-Christine Krämer", 
    "recode_year": 1985 
} 

let font, flip = false,
	offx = 0, offy = 0
	ts = 2500

function preload() {
	font = loadFont('data/fonts/Spartan-SemiBold.ttf')
}

function setup() {
	createCanvas(windowWidth, windowHeight)
	angleMode(DEGREES)
	textFont(font)
	ts = height*4
	guiSetup()
}

function draw() {
	background(sliderColor.value(), 255-sliderAlpha.value())

	if(ts > 1000) {
		ts -= 5
	} else if(ts > height/5) {
		ts -= 3
	}

	sliderScale.value(ts)

	textSize(ts)
	textAlign(CENTER, CENTER)
	fill(255-sliderColor.value())
	translate(width / 2, height / 2)

	if(frameCount % sliderFlip.value() == 0) {
		flip = !flip
	}


	let speed = frameCount * sliderSpeed.value()
	rotate(ceil(speed / sliderSteps.value()) * sliderSteps.value())
	offx = -ts/20
	offy = -ts/7
	translate(offx + sliderOffset.value(), offy+ sliderOffset.value())
	if(flip) {
		text('reactive', 0, 0)
	} else {
		text('recode', textWidth('REACTIVE') * .075, 0)
	}
}


function guiReset(){
	ts = height*4
	sliderScale.value(ts)
	sliderSpeed.value(8)
	sliderSteps.value(30)
	sliderOffset.value(0)
	sliderColor.value(0)
	sliderAlpha.value(5)
}

function guiRandom(){
	ts = random(height*4)
	sliderScale.value(ts)
	sliderSpeed.value(random(sliderSpeed.attribute('min'), sliderSpeed.attribute('max')))
	sliderSteps.value(random(sliderSteps.attribute('min'), sliderSteps.attribute('max')))
	sliderOffset.value(random(sliderOffset.attribute('min'), sliderOffset.attribute('max')))
	sliderOffset.value(random(sliderOffset.attribute('min'), sliderOffset.attribute('max')))
	sliderColor.value(random(sliderColor.attribute('min'), sliderColor.attribute('max')))
	sliderAlpha.value(random(sliderAlpha.attribute('min'), sliderAlpha.attribute('max')))
}

function guiSetup(){
	guiHolder = createElement('div')
	guiHolder.class('guiHolder')

	// LETTER
	createElement('div', 'L E T T E R')
	.parent(guiHolder).class('header')

	createElement('div', 'SCALE')
	.parent(guiHolder).class('label')
	sliderScale = createSlider(height/5, ts, ts, 1)
	.parent(guiHolder)
	.input(function(){
		ts = this.value()
	})

	createElement('div', 'FLIP')
	.parent(guiHolder).class('label')
	sliderFlip = createSlider(1, 100, 20, 1)
	.parent(guiHolder)

	createElement('div', 'SPEED')
	.parent(guiHolder).class('label')
	sliderSpeed = createSlider(1, 100, 8, 1)
	.parent(guiHolder)

	createElement('div', 'STEPS')
	.parent(guiHolder).class('label')
	sliderSteps = createSlider(1, 100, 30, 1)
	.parent(guiHolder)

	createElement('div', 'OFFSET')
	.parent(guiHolder).class('label')
	sliderOffset = createSlider(-300, 300, 0, 1)
	.parent(guiHolder)


	// FORM
	createElement('div', 'F O R M')
	.parent(guiHolder).class('header')

	createElement('div', 'COLOR')
	.parent(guiHolder).class('label')
	sliderColor = createSlider(0, 255, 0, 1)
	.parent(guiHolder)

	createElement('div', 'TRACE')
	.parent(guiHolder).class('label')
	sliderAlpha = createSlider(1, 255, 5, 1)
	.parent(guiHolder)

	createButton('RANDOM')
	.parent(guiHolder)
	.mousePressed(function(){
		guiRandom()
	})

	createButton('RESET')
	.parent(guiHolder)
	.mousePressed(function(){
		guiReset()
	})

	createElement('style', `
		*{
			box-sizing:border-box;
		}
		.guiHolder{
			position:fixed;
			right:10px;
			top:50px;
			width:150px;
			// height:200px;
			border-radius:5px;
			background:red;
			color:#fff;
			padding:10px;
			font-family:sans-serif;
			background:rgba(0, 0, 0, .7)
		}
		.header{
			font-size:12pt;
			text-align:justify;
			margin: 15px 0 5px 0;

		}
		.label{
			margin-top:5px;
			font-size:9pt;
		}
		button{
			margin:15px 3px 0 !important;
			padding:1px 3px 1px 3px;
			float:left;
			font-size:9pt;
			margin:0;
		}
	`)
}
