let font, flip = false,
	offx = 0, offy = 0
	ts = 2500

function preload() {
	font = loadFont('data/fonts/Spartan-SemiBold.ttf')
}

function setup() {
	createCanvas(windowWidth, windowHeight)
	angleMode(DEGREES)
	textFont(font)
	ts = height*4
}

function draw() {
	background(0, 250)

	if(ts > 1000) {
		ts -= 5
	} else if(ts > height/5) {
		ts -= 3
	}

	textSize(ts)
	textAlign(CENTER, CENTER)
	fill(255)
	translate(width / 2, height / 2)

	if(frameCount % 20 == 0) {
		flip = !flip
	}


	let speed = frameCount * 8
	rotate(ceil(speed / 30) * 30)
	offx = -ts/20
	offy = -ts/7
	translate(offx, offy)
	if(flip) {
		text('reactive', 0, 0)
	} else {
		text('recode', textWidth('REACTIVE') * .075, 0)
	}
}
