let reactives = []
let recodes = [],
	rmax = 15

let offx = 0,
	offy = 0
ts = 2500

function setup() {
	createCanvas(windowWidth, windowHeight)
	guiSetup()

	for(let i=0; i<5;i++){
	reactives.push(new Runner('reactive', 255))
	recodes.push(new Runner('recode', 0))
	}

}

function draw() {
	background(0, 255 - sliderTrace.value())
	for(let r of reactives) {
		r.display()
	}
	for(let r of recodes) {
		r.display()
	}

	if(frameCount % 240 == 0) {
		reactives.push(new Runner('reactive', 255))
		recodes.push(new Runner('recode', 0))
	}

	print(reactives.length)
	if(reactives.length > rmax) {
		reactives.splice(0, 1)
		recodes.splice(0, 1)
	}
}

class Runner {
	constructor(txt, fc) {
		this.txt = txt
		this.x = random(-width * 2, -width / 2)
		this.yOff = 7
		this.y = ceil(random(this.yOff)) * height / this.yOff
		this.speed = random(2, 25)
		this.size = height / this.yOff * 1.75
		this.fill = fc
	}
	display() {
		fill(this.fill)
		textAlign(CENTER, CENTER)
		textSize(this.size*sliderScale.value())
		// randomSeed(4)
		this.x += this.speed * sliderSpeed.value()
		if(this.x > width * 2) {
			this.y = ceil(random(this.yOff)) * height / this.yOff
			this.x = -width

		}
		// let y = height/2
		stroke(this.fill)
		strokeWeight(8)
		text(this.txt, this.x, this.y)
	}
}


