let credits = {
	"students_name": "judy kessba, michelle m√ºller",
	"recode_id": "zotero2-2604593.HXFCTX5F",
	"recode_timestamp": 27,
	"recode_title": "ID_Linienraster",
	"recode_designer": "Arx, Manz",
	"recode_year": 0
}

let guiHolder
let lines, id, density, lines2
let slider
let sliderDensity, sliderTextSize
let rot = 0
let colorPicker
let colorPicker2
let colorPicker3
let font
let txt = 'ID'




function preload () {
font= loadFont("data/judy_michelle/Futura.ttf")
}

function setup() {
	createCanvas(windowWidth, windowHeight)
	lines = createGraphics(width * 2, height * 2)
	lines2 = createGraphics(width * 2, height * 2)
	id = createGraphics(width * 2, height * 2)
	imageMode(CENTER)



	// create generic div (html container) 
	guiHolder = createDiv()
		.class('guiHolder') // add class for styling via CSS 


	let myInput = createInput(txt)
		.parent(guiHolder) // place inside gui div 
	myInput.attribute('placeholder', 'type some text here')

	myInput.input(function() { // react immediately via 'anonymous function' 
		txt = this.value(); // update txt value 

	});


	// use styled div as label 
	createDiv('LINE SPACING')
		.parent(guiHolder) // place inside gui div 
		.class('label') // class for CSS 

	// create generic slider 
	sliderDensity = createSlider(0, 100, 0)
		.parent(guiHolder) //  place inside gui div 
		.class('slider') // class for CSS 
		.input(function() {
			background(0, 0, this.value()) // use immediately, basic demo 
		})


	// use styled div as label 
	createDiv('TEXT SIZE')
		.parent(guiHolder) // place inside gui div 
		.class('label') // class for CSS 

	sliderTextSize = createSlider(100, 600, 600)
		.parent(guiHolder) //  place inside gui div 
		.class('slider') // class for CSS 
		.input(function() {
			background(0, 0, this.value()) // use immediately, basic demo 
		})


	// use styled div as label 
	createDiv('SPIN')
		.parent(guiHolder) // place inside gui div 
		.class('label') // class for CSS 


	slider = createSlider(-25, 25, .01, .01)
		.parent(guiHolder) //  place inside gui div 
		.class('slider') // class for CSS 
		.input(function() {
			background(0, 0, this.value()) // use immediately, basic demo 
		})


	createDiv('COLOR')
		.parent(guiHolder) // place inside gui div 
		.class('label') // class for CSS 
	colorPicker = createColorPicker(1)
		.parent(guiHolder)
	//	colorPicker.position(397, 80);

	colorPicker2 = createColorPicker(1)
		.parent(guiHolder)
	//colorPicker2.position(397, 120);

	colorPicker3 = createColorPicker(255)
		.parent(guiHolder)
	//colorPicker2.position(397, 120);


	// create <style> tag for custom css (note `backticks` for multi-line value!) 
	createElement('style', ` 
      *{ 
        outline: none; 
        box-sizing: border-box; 
      } 
        .guiHolder{ 
          position: fixed; 
          right: 50px; 
          top: 50px; 
          //border: 2px solid #fff; 
          //border-radius: 10px; 
          padding: 10px; 
        } 
        .label{ 
          color:#000; 
          background:#fff;
          font-family:font; 
          font-size:10pt; 
          //text-decoration:underline; 
          padding:5px; 
        } 
        .slider{ 
          -webkit-appearance: none;
          width: 100%; 
          background: #000;
          opacity:90%;
          height: 6px;
          border-radius: 10px;

        } 
        `)

	angleMode(DEGREES)
}


function draw() {
	background(colorPicker3.color())
	//lines background
	lines.strokeWeight(2)
	lines.stroke(colorPicker.color())
	//for(let i=0; i<width; i++){
	//	lines.line(i*density,0,i*density,height)
	lines.clear()
	for(let i = 0; i < 150; i++) {
		let x = map(i, sliderDensity.value(), 150, 0, lines.width)
		lines.line(x, 0, x, lines.height);
	}

	//lines type
	lines2.strokeWeight(2)
	lines2.stroke(colorPicker2.color())
	//for(let i=0; i<width; i++){
	//	lines.line(i*density,0,i*density,height)
	lines2.clear()
	for(let i = 0; i < 150; i++) {
		let x = map(i, sliderDensity.value(), 150, 0, lines2.width)
		lines2.line(x, 0, x, lines2.height);
	}



	id.clear()
	id.image(lines2, 0, 0)
	id.erase()
	let myWord = 'ID'
	id.textSize(sliderTextSize.value()) // calculate automagically, height/myWord.length... something like that
	id.textAlign(CENTER, CENTER)
	id.textFont(font)
	id.text(txt, id.width / 2, id.height / 2)
	id.noErase()

	translate(width / 2, height / 2)
	push()
	rot += slider.value()
	rotate(rot)
	image(lines, 0, 0)
	pop()
	image(id, 0, 0)
}
